import React from 'react';
import Job from './Job';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import MobileStepper from '@material-ui/core/MobileStepper';
import JobModal from './JobModal';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';


export default function Jobs({ jobs }) {

    // modal
    const [open, setOpen] = React.useState(false);
    const [selectedJob, selectJob] = React.useState({});

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    // pagination
    const [activeStep, setActiveStep] = React.useState(0);
    const numJobs = jobs.length;
    const numPages = Math.ceil(numJobs / 50);
    const jobsOnPage = jobs.slice(activeStep * 50, (activeStep * 50) + 50);

    // get distinct locations, job types, and dates posted for all jobs
    const [jobsShown, setJobsShown] = React.useState(jobsOnPage);
    const locations = [];
    const jobTypes = [];
    const datesPosted = [];

    jobs.forEach(job => {
        if (locations.includes(job.location)) return;
        locations.push(job.location);

        if (jobTypes.includes(job.type)) return;
        jobTypes.push(job.type);

        if (datesPosted.includes(job.created_at)) return;
        datesPosted.push(job.created_at);
    });

    function handleNext() {
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    function handleBack() {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    function handleLocationChange(event) {
        const newJobs = jobs.filter(job => {
            return job.location == event.target.value;
        });
        setJobsShown(jobsShown => newJobs);
    }

    function handleTypeChange(event) {
        const newJobs = jobs.filter(job => {
            return job.type == event.target.value;
        });
        const newObj = {  }

        setJobsShown(newJobs);
    }

    function handleDateChange(event) {
        const newJobs = jobs.filter(job => {
            return job.created_at == event.target.value;
        });
        setJobsShown(newJobs);
    }

    // set random key for each job listing
    function generateId(length) {
        var result = '';
        var characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        var charactersLength = characters.length;
        for (var i = 0; i < length; i++) {
            result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        return result;
    }

    const filterStyle = makeStyles({
        field: {
            width: '120px',
            margin: '20px',
        },

        textField: {
            margin: 'auto',
            marginRight: '320px',
        }
    });

    const filterContainerStyle = {
        width: '170px',
        marginTop: '200px',
        border: '2px solid black',
        position: 'absolute',
        backgroundColor: 'white',
    }

    const style = filterStyle();

    return (
        <div className='jobs'>
            <div className='filters col-sm-2' style={filterContainerStyle}>
                <Autocomplete
                    className={style.field}
                    id="location-filter"
                    options={locations}
                    onChange={handleLocationChange}
                    renderInput={(params) => <TextField className={style.textField} {...params} label="Location" variant="outlined" autoComplete={true} />}
                />
                <Autocomplete
                    className={style.field}
                    id="type-filter"
                    options={jobTypes}
                    onChange={handleTypeChange}
                    renderInput={(params) => <TextField className={style.textField} {...params} label="Job Type" variant="outlined" autoComplete={true} />}
                />
                <Autocomplete
                    className={style.field}
                    id="location-filter"
                    options={datesPosted}
                    onChange={handleDateChange}
                    renderInput={(params) => <TextField className={style.textField} {...params} label="Date Posted" variant="outlined" autoComplete={true} />}
                />
            </div>

            <JobModal open={open} job={selectedJob} handleClose={handleClose} />
            <Typography variant='h4' component='h1' style={{ color: 'white', fontSize: '27px' }}>
                Junior Level Software Engineer Jobs
            </Typography>
            <Typography variant='h6' component='h1' style={{ color: 'white', fontSize: '16px' }}>
                {numJobs} jobs found.
            </Typography>

            {jobsOnPage.map((job, i) => <Job job={job} key={generateId(6)} onClick={() => {
                selectJob(job);
                handleClickOpen();
            }} />)}

            <div id='page-num' style={{ padding: '8px' }}>
                Page {activeStep + 1} of {numPages}
            </div>

            <MobileStepper
                variant="progress"
                steps={Math.ceil(numJobs / 50)}
                position="static"
                activeStep={activeStep}
                nextButton={
                    <Button size="small" onClick={handleNext} disabled={activeStep === 5}>
                        Next
                        <KeyboardArrowRight />
                    </Button>
                }
                backButton={
                    <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
                        <KeyboardArrowLeft />
                        Back
                    </Button>
                }
            />
        </div>
    )
}
