import React from 'react';
import Typography from '@material-ui/core/Typography';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core';

const styles = makeStyles({
    text: {
        fontFamily: 'Encode Sans',
    }
});

export default function Job({ job, onClick }) {
    const classes = styles();
    return (
        <Paper className='job' onClick={onClick}>
            <div className='job-content'>
                <Typography className={classes.text} variant='h5' style={{fontSize: '20px'}}>{job.title}</Typography>
                <Typography className={classes.text} variant='h6' style={{fontSize: '14px'}}>{job.company}</Typography>
                <Typography className={classes.text} style={{fontSize: '12px'}}>{job.location}</Typography>
            </div>

            <div>
                <Typography className={classes.text} style={{fontSize: '12px'}}>Posted on {job.created_at.split(' ').slice(0, 3).join(' ')}</Typography>
            </div>
        </Paper>
    )
}
