import React from 'react';
import './App.css';
import Jobs from './Jobs';

const JOB_API_URL = 'http://localhost:3001/jobs';

const sampleJobs = [
  { title: 'Entry Level Software Engineer', company: 'Apple' },
  { title: 'Junior Front End Developer', company: 'Salesforce' },
  { title: 'Junior Front End Engineer', company: 'JP Morgan' },
  { title: 'Mid Level Full-Stack Engineer', company: 'Google' }
];

async function fetchJobs(updateCb) {
  const res = await fetch(JOB_API_URL);
  const json = await res.json();

  updateCb(json);

  console.log({ json });
}

function App() {

  const [jobList, updateJobs] = React.useState([])

  React.useEffect(() => {
    fetchJobs(updateJobs);
  }, [])

  return (
    <div className="App" style={{backgroundColor: '#6495ED', height: '100vh'}}>
      <Jobs jobs={jobList} />
    </div>
  );
}

export default App;
