import React from 'react';
import './App.css';
import Jobs from './Jobs';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';

const JOB_API_URL = 'http://localhost:3001/jobs';

const sampleJobs = [
  { title: 'Entry Level Software Engineer', company: 'Apple' },
  { title: 'Junior Front End Developer', company: 'Salesforce' },
  { title: 'Junior Front End Engineer', company: 'JP Morgan' },
  { title: 'Mid Level Full-Stack Engineer', company: 'Google' }
];

async function fetchJobs(updateCb) {
  const res = await fetch(JOB_API_URL);
  const json = await res.json();

  updateCb(json);

  console.log({ json });
}

// filter by location(location), job type(type), date posted (created_at)
const locations = [];
const jobTypes = [];
const datesPosted = [];

jobList.forEach(job => {
  if (locations.includes(job.location)) return;
  locations.push(job.location);

  if (jobTypes.includes(job.type)) return;
  jobTypes.push(job.type);

  if (datesPosted.includes(job.created_at)) return;
  datesPosted.push(job.created_at);
});

const filterStyle = makeStyles({
  field: {
      width: '300px',
  },
})

const style = filterStyle();

function App() {

  const [jobList, updateJobs] = React.useState([])

  React.useEffect(() => {
    fetchJobs(updateJobs);
  }, [])

  return (
    <div className="App" style={{backgroundColor: '#6495ED'}}>
      <div className='filters'>
                <Autocomplete
                    className={style.field}
                    id="location-filter"
                    options={locations}
                    renderInput={(params) => <TextField {...params} label="Location" variant="outlined" autoComplete={true} />}
                />
                <Autocomplete
                    className={style.field}
                    id="type-filter"
                    options={jobTypes}
                    renderInput={(params) => <TextField {...params} label="Job Type" variant="outlined" autoComplete={true} />}
                />
                <Autocomplete
                    className={style.field}
                    id="location-filter"
                    options={datesPosted}
                    renderInput={(params) => <TextField {...params} label="Date Posted" variant="outlined" autoComplete={true} />}
                />
            </div>

      <Jobs jobs={jobList} />
    </div>
  );
}

export default App;
