import React, { useState, useEffect } from 'react';
import Job from './Job';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import MobileStepper from '@material-ui/core/MobileStepper';
import JobModal from './JobModal';
import TextField from '@material-ui/core/TextField';
import Autocomplete from '@material-ui/lab/Autocomplete';
import Case from './work.svg';


export default function Jobs({ jobs }) {

    const [noResults, setNoResults] = useState(false);

    // modal
    const [open, setOpen] = useState(false);
    const [selectedJob, selectJob] = useState({});

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    // pagination (FIX THIS)
    const [activeStep, setActiveStep] = useState(0);
    const numJobs = jobs.length;
    const numPages = Math.ceil(numJobs / 20);
    const [numberPages, setNumPages] = useState(0);
    const jobsOnPage = jobs.slice(activeStep * 20, (activeStep * 20) + 20);

    // get distinct locations, job types, and dates posted for all jobs
    const [jobsShown, setJobsShown] = useState([]);
    const locations = [];
    const jobTypes = [];
    const datesPosted = [];

    jobs.forEach(job => {
        if (locations.includes(job.location)) return;
        locations.push(job.location);

        if (jobTypes.includes(job.type)) return;
        jobTypes.push(job.type);

        if (datesPosted.includes(job.created_at)) return;
        datesPosted.push(job.created_at);
    });

    useEffect(() => {
        setJobsShown(jobsOnPage);
        setNumPages(numPages);
    }, [numJobs, activeStep])


    function handleNext() {
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    function handleBack() {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    function handleFilter(e, val) {
        if (val === null) {
            setJobsShown(jobsOnPage);
        } else {
            let results, locJobs, typeJobs, dateJobs = [];
            switch(e.target.dataset.optionIndex) {
                case 0:
                    locJobs = jobs.filter(job => {
                        return job.location === val;
                    });
                    console.log(locJobs)
                    break;
                case 1:
                    typeJobs = jobs.filter(job => {
                        return job.type === val;
                    });
                    break;
                case 2:
                    dateJobs = jobs.filter(job => {
                        return job.created_at === val;
                    });
                    break;
            }

            if (locJobs) {
                for (let job of locJobs) {
                    results.push(job);
                }
            }

            if (typeJobs) {
                for (let job of typeJobs) {
                    results.push(job);
                }
            }
            if (dateJobs) {
                for (let job of dateJobs) {
                    results.push(job);
                }
            }

            //setJobsShown(results);
        }
    }

    function handleLocationChange(event, value) {
        if (value === null) {
            setJobsShown(jobsOnPage);
        } else {

            const newJobs = jobs.filter(job => {
                return job.location === value;
            });

            setJobsShown(newJobs);
            setNumPages(Math.ceil(jobsShown.length / 20));
        }
    }

    function handleTypeChange(event, value) {
        let newJobs;
        if (value === null) {
            setJobsShown(jobsOnPage);
        } else {


            newJobs = jobs.filter(job => {
                return job.type === value;
            });

            // const newJobs = jobs.filter(job => {
            //     return job.type === value;
            // });

            if (newJobs.length === 0) {
                setNoResults(true);
            }

            setJobsShown(newJobs);
            setNumPages(Math.ceil(jobsShown.length / 20));
        }
    }

    function handleDateChange(event, value) {
        if (value === null) {
            setJobsShown(jobsOnPage);
        } else {

            const newJobs = jobs.filter(job => {
                return job.created_at === value;
            });

            setJobsShown(newJobs);
            setNumPages(Math.ceil(jobsShown.length / 20));
        }
    }

    // set random key for each job listing
    function generateId(length) {
        var result = '';
        var characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        var charactersLength = characters.length;
        for (var i = 0; i < length; i++) {
            result += characters.charAt(Math.floor(Math.random() * charactersLength));
        }
        return result;
    }

    const filterStyle = makeStyles({
        field: {
            width: '120px',
            margin: '20px',
        },
    });

    const filterContainerStyle = {
        width: '200px',
        marginTop: '200px',
        border: '2px solid black',
        position: 'absolute',
        backgroundColor: 'white',
        borderRadius: '0px 25px 25px 0px',
        zIndex: '2',
    }

    const style = filterStyle();

    return (
        <div className='jobs'>
            <div className='filters col-sm-2' style={filterContainerStyle}>
                <Autocomplete
                    className={style.field}
                    id="location-filter"
                    options={locations}
                    onChange={handleFilter}
                    renderInput={(params) => <TextField {...params} label="Location" variant="outlined" autoComplete={true} />}
                />
                <Autocomplete
                    className={style.field}
                    id="type-filter"
                    options={jobTypes}
                    onChange={handleFilter}
                    renderInput={(params) => <TextField {...params} label="Job Type" variant="outlined" autoComplete={true} />}
                />
                <Autocomplete
                    className={style.field}
                    id="date-filter"
                    options={datesPosted}
                    onChange={handleFilter}
                    renderInput={(params) => <TextField {...params} label="Date Posted" variant="outlined" autoComplete={true} />}
                />
            </div>

            <JobModal open={open} job={selectedJob} handleClose={handleClose} />
            <div style={{ padding: '15px' }}>
                <Typography variant='h4' component='h1' style={{ color: 'white', fontFamily: 'Encode Sans', fontSize: '27px', textAlign: 'left', marginLeft: '8px' }}>
                    Junior Level Software Developer Jobs <img src={Case} style={{ width: '35px' }} />
                </Typography>
                <Typography variant='h6' component='h1' style={{ color: 'white', fontFamily: 'Encode Sans', fontSize: '16px', textAlign: 'left', marginLeft: '8px' }}>
                    {numJobs} jobs found.
            </Typography>
            </div>

            {jobsShown.map((job) => <Job job={job} key={generateId(6)} onClick={() => {
                selectJob(job);
                handleClickOpen();
            }} />)}
            {noResults === true && <Typography variant='h1'>No results.</Typography>}

            <div id='page-num' style={{ padding: '8px' }}>
                Page {activeStep + 1} of {numberPages}
            </div>

            <MobileStepper
                variant="progress"
                steps={Math.ceil(numJobs / 50)}
                position="static"
                activeStep={activeStep}
                nextButton={
                    <Button size="small" onClick={handleNext} disabled={activeStep === 5 || activeStep + 1 === numberPages}>
                        Next
                        <KeyboardArrowRight />
                    </Button>
                }
                backButton={
                    <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
                        <KeyboardArrowLeft />
                        Back
                    </Button>
                }
            />
        </div>
    )
}
