import React, { useState, useEffect } from 'react';
import Job from './Job';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import MobileStepper from '@material-ui/core/MobileStepper';
import JobModal from './JobModal';
import Case from './work.svg';
import { JobList } from './styles';


export default function Jobs({ jobs }) {
    // modal
    const [open, setOpen] = useState(false);
    const [selectedJob, selectJob] = useState({});

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };
    // job list
    const [noResults, setNoResults] = useState(false);
    const [activeStep, setActiveStep] = useState(0);
    const numJobs = jobs.length;
    const numPages = Math.floor(numJobs / 10);
    const [numberPages, setNumPages] = useState(0);
    const offset = 10 * activeStep;
    const jobsOnPage = jobs.slice(offset, offset + 10);

    const [jobsShown, setJobsShown] = useState([]);

    useEffect(() => {
        setJobsShown(jobsOnPage);
        setNumPages(numPages);
    }, [numJobs, activeStep])


    function handleNext() {
        setActiveStep(prevActiveStep => prevActiveStep + 1);
    }

    function handleBack() {
        setActiveStep(prevActiveStep => prevActiveStep - 1);
    }

    const onJobSelect = (job) => {
        selectJob(job);
        handleClickOpen();
    }

    return (
        <div className='jobs'>
            <JobModal open={open} job={selectedJob} handleClose={handleClose} />
            <div style={{ padding: '15px', textAlign: "left" }}>
                <Typography variant='h4' component='h1' style={{ color: 'white', fontFamily: 'Encode Sans', fontSize: '27px', marginLeft: '8px' }}>
                    Junior Level Software Developer Jobs <img src={Case} style={{ width: '35px' }} />
                </Typography>
                <Typography variant='h6' component='h1' style={{ color: 'white', fontFamily: 'Encode Sans', fontSize: '16px', marginLeft: '8px' }}>
                    {numJobs} jobs found.
            </Typography>
            </div>

            <JobList>
                {jobsShown.map((job, idx) => <Job job={job} key={idx} onSelect={onJobSelect} />)}
            </JobList>

            {noResults === true ? <Typography variant='h1'>No results.</Typography> : null}

            <div id='page-num' style={{ padding: '8px' }}>
                Page {activeStep + 1} of {numPages}
            </div>

            <MobileStepper
                variant="progress"
                steps={Math.ceil(numJobs / 10)}
                position="static"
                activeStep={activeStep}
                nextButton={
                    <Button size="small" onClick={handleNext} disabled={activeStep + 1 === numberPages}>
                        Next
                        <KeyboardArrowRight />
                    </Button>
                }
                backButton={
                    <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
                        <KeyboardArrowLeft />
                        Back
                    </Button>
                }
            />
        </div>
    )
}
